
-- Skill axis library migration (versioned + localization)
-- Generated from Axis Library.csv on 2026-01-10
set check_function_bodies = off;

create type axis_status as enum ('active','deprecated');
create type axis_type as enum ('soft_skill','hard_skill','work_style','constraint','motivation');

create table skill_axis_versions (
  id uuid primary key default gen_random_uuid(),
  axis_key text not null check (axis_key ~ '^[a-z0-9_]+$'),
  axis_type axis_type not null,
  role_families text[] not null,
  synonyms text[] not null default '{}'::text[],
  rubric jsonb not null,
  evidence_types text[] not null default '{}'::text[],
  assessment_options text[] not null default '{}'::text[],
  version int not null,
  status axis_status not null default 'active',
  typical_roles text[] not null default '{}'::text[],
  created_at timestamptz not null default now()
);

create table skill_axis_localizations (
  id uuid primary key default gen_random_uuid(),
  axis_version_id uuid not null references skill_axis_versions(id) on delete cascade,
  locale text not null,
  display_name text not null,
  definition text not null,
  not_definition text not null,
  created_at timestamptz not null default now(),
  unique (axis_version_id, locale)
);

create view skill_axes_latest as
select
  sav.id as axis_version_id,
  sav.axis_key,
  sav.axis_type,
  sav.role_families,
  sav.synonyms,
  sav.rubric,
  sav.evidence_types,
  sav.assessment_options,
  sav.version,
  sav.status,
  sav.typical_roles,
  sal.locale,
  sal.display_name,
  sal.definition,
  sal.not_definition
from skill_axis_versions sav
join skill_axis_localizations sal on sal.axis_version_id = sav.id and sal.locale = 'en'
where sav.status = 'active';

